annotations.alm-examples:
[
  {
    "apiVersion": "camel.apache.org/v1",
    "kind": "Build",
    "metadata": {
      "name": "example"
    },
    "spec": {}
  },
  {
    "apiVersion": "camel.apache.org/v1",
    "kind": "CamelCatalog",
    "metadata": {
      "name": "example"
    },
    "spec": {}
  },
  {
    "apiVersion": "camel.apache.org/v1",
    "kind": "Integration",
    "metadata": {
      "name": "example"
    },
    "spec": {
      "flows": [
        {
          "from": {
            "parameters": {
              "period": "1000"
            },
            "steps": [
              {
                "set-body": {
                  "constant": "Hello from Camel K"
                }
              },
              {
                "to": "log:info"
              }
            ],
            "uri": "timer:yaml"
          }
        }
      ]
    }
  },
  {
    "apiVersion": "camel.apache.org/v1",
    "kind": "IntegrationKit",
    "metadata": {
      "name": "example"
    },
    "spec": {}
  },
  {
    "apiVersion": "camel.apache.org/v1",
    "kind": "IntegrationPlatform",
    "metadata": {
      "labels": {
        "app": "camel-k"
      },
      "name": "camel-k"
    },
    "spec": {
      "build": {},
      "kamelet": {},
      "profile": "OpenShift",
      "resources": {}
    },
    "status": {
      "build": {
        "maven": {
          "settings": {}
        },
        "registry": {}
      },
      "kamelet": {},
      "resources": {}
    }
  },
  {
    "apiVersion": "camel.apache.org/v1alpha1",
    "kind": "Kamelet",
    "metadata": {
      "name": "example"
    },
    "spec": {
      "definition": {
        "description": "Produces periodic events with a custom payload",
        "properties": {
          "message": {
            "description": "The message to generate",
            "title": "Message",
            "type": "string"
          },
          "period": {
            "default": 1000,
            "description": "The time interval between two events",
            "title": "Period",
            "type": "integer"
          }
        },
        "required": [
          "message"
        ],
        "title": "Example Timer"
      },
      "template": {
        "from": {
          "parameters": {
            "period": "#property:period"
          },
          "steps": [
            {
              "set-body": {
                "constant": "#property:message"
              }
            },
            {
              "to": "kamelet:sink"
            }
          ],
          "uri": "timer:tick"
        }
      }
    }
  },
  {
    "apiVersion": "camel.apache.org/v1alpha1",
    "kind": "KameletBinding",
    "metadata": {
      "name": "example"
    },
    "spec": {
      "sink": {
        "ref": {
          "apiVersion": "messaging.knative.dev/v1",
          "kind": "InMemoryChannel",
          "name": "example"
        }
      },
      "source": {
        "properties": {
          "message": "Hello world"
        },
        "ref": {
          "apiVersion": "camel.apache.org/v1alpha1",
          "kind": "Kamelet",
          "name": "example"
        }
      }
    }
  }
]

