[
  {
    "apiVersion": "org.eclipse.che/v1",
    "kind": "CheCluster",
    "metadata": {
      "name": "devspaces",
      "namespace": "openshift-devspaces"
    },
    "spec": {
      "auth": {
        "identityProviderURL": "",
        "oAuthClientName": "",
        "oAuthSecret": ""
      },
      "database": {
        "externalDb": false
      },
      "k8s": {
        "ingressDomain": null,
        "tlsSecretName": null
      },
      "metrics": {
        "enable": true
      },
      "server": {
        "workspaceNamespaceDefault": "<username>-devspaces"
      },
      "storage": {
        "pvcStrategy": "common"
      }
    }
  },
  {
    "apiVersion": "org.eclipse.che/v2",
    "kind": "CheCluster",
    "metadata": {
      "name": "devspaces",
      "namespace": "openshift-devspaces"
    },
    "spec": {
      "components": {
        "dashboard": {
          "headerMessage": {
            "show": true,
            "text": "Microsoft Visual Studio Code - Open Source is the default <a href='https://access.redhat.com/documentation/en-us/red_hat_openshift_dev_spaces/3.4/html-single/user_guide/index#selecting-a-workspace-ide'>editor</a> for new workspaces. Eclipse Theia is <a href='https://access.redhat.com/documentation/en-us/red_hat_openshift_dev_spaces/3.4/html-single/release_notes_and_known_issues/index#deprecated-functionality-crw-3405'>deprecated</a> and will be removed in a future release."
          }
        }
      },
      "containerRegistry": {},
      "devEnvironments": {},
      "gitServices": {},
      "networking": {}
    }
  }
]

